import React from 'react';
import Location from './location';
import Model from './model';
import Extra from './extra';
import Total from './total';
import Info  from './info';

class Order extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            city: "",
            point: "",
            model: "3",
            color: "",
            date: "",
            tariff: "",
            service: {
                tank: "",
                chair: "",
                rudder: ""
            },
            activeTab: {
                location: true,
                model: false,
                extra: false,
                total: false
            },
        }
    }

    changeCity(value) {
        this.setState({
            city: value
        })
    }

    changePoint(value) {
        this.setState({
            point: value
        })
    }

    changeModel(value) {
        this.setState({
            model: value
        })
    } 
    changeExtra(value) {
        this.setState({
            point: value
        })
    }

    render() {
        console.log(this.state.point)
        console.log(this.state.city)
        console.log(this.state.model)
        let tab
        if (this.props.activeTab == 'location' || this.state.point == '') {
            tab = <Location 
                onChangePoint={(value) => this.changePoint(value)}
                onChangeCity={(value) => this.changeCity(value)}
                city={this.state.city}
                point={this.state.point}
                activ={this.state.point}
            />
        } else if (this.props.activeTab == 'model') {
            tab = <Model onChange={() => this.changeModel()}/>
        } else if (this.props.activeTab == 'extra') {
            tab = <Extra onChange={() => this.changeExtra()}/>
        } else {
            tab = <Total />
        }  
        return (
            <div class="order">
               <div class="board-order">
                    {tab}
                </div>
                <Info options={this.state.options}/>
            </div>
        );
    }
}

export default Order;
