import React from 'react';
import popupShow from './popupShow';

class Info extends React.Component {
    constructor(props) {
        super(props);
    }

    renderButton(check, tab, text) {
        if (tab && check) {
            return (
            <button onClick={() => this.props.onClick(tab)}>{text}</button>
            )
        } else {
            return (
                <button disabled>{text}</button>
            )
        }
    }

    renderExtra() {}

    render() {
        let tab
        if (this.props.location.city !== "" && this.props.location.point !== "" && this.props.activeTab.location) {
            tab = "model"
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     <div class="info__point"> 
                         <span>Пункт выдачи</span>
                         <span></span>
                        <span>{this.props.location.city}, {this.props.location.point}</span>
                     </div>
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     {this.renderButton(this.props.location.point, tab, "Выбрать модель")}
                </div>
            );
        } else if (this.props.location.city !== "" && this.props.location.point !== "" && this.props.activeTab.model) {
            tab = "extra"
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     <div class="info__point"> 
                         <span>Пункт выдачи</span>
                         <span></span>
                        <span>{this.props.location.city}, {this.props.location.point}</span>
                     </div>
                     <div class="info__point"> 
                         <span>Модель</span>
                         <span></span>
                        <span>{this.props.car.mark}, {this.props.car.model}</span>
                     </div>
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     {this.renderButton(this.props.car.model, tab, "Дополнительно")}
                </div>
            );
        } else if (this.props.location.city !== "" && this.props.location.point !== "" && this.props.activeTab.extra) {
            tab = "total"
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     <div class="info__point"> 
                         <span>Пункт выдачи</span>
                         <span></span>
                        <span>{this.props.location.city}, {this.props.location.point}</span>
                     </div>
                     <div class="info__point"> 
                         <span>Модель</span>
                         <span></span>
                        <span>{this.props.car.mark}, {this.props.car.model}</span>
                     </div>
                     <div class="info__point"> 
                        <span>Цвет</span>
                        <span></span>
                        <span>{this.props.location.city}, {this.props.location.point}</span>
                     </div>
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     {this.renderButton(this.props.extra.date, tab, "Итого")}
                </div>
            );
        } else if (this.props.location.city !== "" && this.props.location.point !== "" && this.props.activeTab.total) {
            tab = "total"
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     <div class="info__point"> 
                        <span>Пункт выдачи</span>
                        <span></span>
                        <span>{this.props.location.city}, {this.props.location.point}</span>
                     </div>
                     <div class="info__point"> 
                         <span>Модель</span>
                         <span></span>
                        <span>{this.props.location.city}, {this.props.location.point}</span>
                     </div>
                     <div class="info__point"> 
                        <span>Цвет</span>
                        <span></span>
                        <span>{this.props.location.city}, {this.props.location.point}</span>
                     </div>
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     {this.renderButton(tab, "Заказать")}
                </div>
            );
        } else {
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     <div class="info__point"> 
                         <span>Пункт выдачи</span>
                         <span></span>
                        <span> - </span>
                     </div>
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     <button disabled>Выбрать модель</button>
                </div>
            );
        }

    }
}

export default Info;
