import React from 'react';
import Elantraimg from '../../assets/img/image 1.png';
import i30img from '../../assets/img/image 2.png';
import Cretaimg from '../../assets/img/image 3.png';
import Sonataimg from '../../assets/img/image 4.png';

class Model extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            all:  true,
            ec: false,
            prem: false,
            models: [
                {
                    model: "ELANTRA",
                    price: "12 000 - 25 000 ₽",
                    img: Elantraimg
                },
                {
                    model: "i30 N",
                    price: "10 000 - 32 000 ₽",
                    img: i30img
                },
                {
                    model: "CRETA",
                    price: "12 000 - 25 000 ₽",
                    img: Cretaimg
                },
                {
                    model: "SONATA",
                    price: "10 000 - 32 000 ₽",
                    img: Sonataimg
                }
            ]
        };

        this.handleChange = this.handleChange.bind(this);
    }

    sectionActive() {
        let a = this;
        console.log(a)
    }

    section(i) {
        return (
            <section class="cars__block" onClick={() => this.sectionActive()}>
                <div class="cars__name">
                    <h2>{this.state.models[i].model}</h2>
                    <p>{this.state.models[i].price}</p>
                </div>
                <img src={this.state.models[i].img} alt=""/>
            </section>
        );
    }

    handleChange(event) {
        const name = event.target.name;

        if (name == "all") {
            this.setState({all: true, ec: false, prem: false,});
        } else if (name == "ec") {
            this.setState({all: false, ec: true, prem: false,});
        } else {
            this.setState({all: false, ec: false, prem: true,});
        }
    }

    render() {
        return (
            <div class="model tab_item">
                <div class="sorting-cars">
                    <input type="radio" checked={this.state.all} onChange={this.handleChange} name="all" id="r1" /><label for="r1"> Все модели </label>
                    <input type="radio" checked={this.state.ec} onChange={this.handleChange} name="ec" id="r2"/><label for="r2"> Эконом </label>
                    <input type="radio" checked={this.state.prem} onChange={this.handleChange} name="prem" id="r3"/><label for="r3"> Премиум </label>
                </div>
                <table class="cars">
                    {this.section(0)}
                    {this.section(1)}
                    {this.section(2)}
                    {this.section(3)}
                    {this.section(0)}
                    {this.section(1)}
                </table>
            </div>
        );
    }
}

export default Model;
