import React from 'react';


class TableCars extends React.Component {
    constructor(props) {
        super(props);
    }

    sectionActive(e) {
        let cars = document.querySelector('.cars');
        console.log(cars);
        car = cars.getElementsByTagName('section');
        for (i = 0; i < car.length; i++) {
            car[i].style.border = "1px solid #EEE";
        }
        console.log(e.target);
        e.target.style.border = `1px solid #0EC261`;
    }

    render() {
        if (this.props.radio.ec) {
            return (
                <div className="cars">
                    {
                        this.props.models.map((model, i) => {
                            if (model.price == "12 000 - 25 000 ₽") {
                                return (
                                    <section class="cars__block" onClick={(e) => this.sectionActive(e)} key={i}>
                                        <div class="cars__name">
                                            <h2>{this.props.models[i].model}</h2>
                                            <p>{this.props.models[i].price}</p>
                                        </div>
                                        <img src={this.props.models[i].img} alt=""/>
                                    </section>
                                );
                            } 
                        })
                    }
                </div>
            );
        } else if (this.props.radio.prem) {
            return (
                <div className="cars">
                    {
                        this.props.models.map((model, i) => {
                            if (model.price == "10 000 - 32 000 ₽") {
                                return (
                                    <section class="cars__block" onClick={(e) => this.sectionActive(e)} key={i}>
                                        <div class="cars__name">
                                            <h2>{this.props.models[i].model}</h2>
                                            <p>{this.props.models[i].price}</p>
                                        </div>
                                        <img src={this.props.models[i].img} alt=""/>
                                    </section>
                                );
                            } 
                        })
                    }
                </div>
            );
        } else {
            return (
                <div className="cars">
                    {
                        this.props.models.map((model, i) => {
                            return (
                                <section class="cars__block" onClick={(e) => this.sectionActive(e)} key={i}>
                                    <div class="cars__name">
                                        <h2>{this.props.models[i].model}</h2>
                                        <p>{this.props.models[i].price}</p>
                                    </div>
                                    <img src={this.props.models[i].img} alt=""/>
                                </section>
                            );
                        })
                    }
                </div>
            );
        }
    }
}

export default TableCars;
