import React from 'react';
import popupShow from './popupShow';

class Info extends React.Component {
    constructor(props) {
        super(props);
    }

    renderButton(check, tab, text) {
        if (tab && check) {
            return (
            <button onClick={() => this.props.onClick(tab)}>{text}</button>
            )
        } else {
            return (
                <button disabled>{text}</button>
            )
        }
    }

    renderExtra(text, value1, value2) {
        let v2;
        if (value2) {
            v2 = ", " + value2
        }
        if (text == "Длительность аренды") {
           let day1 = value1[0].slice(0, 1)
           let month1 = value1[0].slice(3, 4)
           let year1 = value1[0].slice(6, 9)
           let day2 = value1[2].slice(0, 1)
           let month2 = value1[2].slice(3, 4)
           let year2 = value1[2].slice(6, 9)
           let day = day2 - day1
           let month = month2 - month1
           let year = year2 - year1
            console.log(day)
            console.log(month)
            console.log(year)
        } 
        return (
            <div class="info__point"> 
                <span>{text}</span>
                <span></span>
                <span>{value1}{v2}</span>
            </div>
        )
    }

    render() {
        let tab
        if (this.props.location.city !== "" && this.props.location.point !== "" && this.props.activeTab.location) {
            tab = "model"
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     {this.renderExtra("Пункт выдачи", this.props.location.city, this.props.location.point)}
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     {this.renderButton(this.props.location.point, tab, "Выбрать модель")}
                </div>
            );
        } else if (this.props.location.city !== "" && this.props.location.point !== "" && this.props.activeTab.model) {
            tab = "extra"
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     {this.renderExtra("Пункт выдачи", this.props.location.city, this.props.location.point)}
                     {this.renderExtra("Модель", this.props.car.mark, this.props.car.model)}
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     {this.renderButton(this.props.car.model, tab, "Дополнительно")}
                </div>
            );
        } else if (this.props.location.city !== "" && this.props.location.point !== "" && this.props.activeTab.extra) {
            tab = "total"
            return (
                <div class="info">
                     <h2>Ваш заказ:</h2>
                     {this.renderExtra("Пункт выдачи", this.props.location.city, this.props.location.point)}
                     {this.renderExtra("Модель", this.props.car.mark, this.props.car.model)}
                     {this.renderExtra("Цвет", this.props.extra.color)}
                     {this.renderExtra("Длительность аренды", this.props.extra.date)}
                     {this.renderExtra("Тариф", this.props.extra.tariff)}
                     {
                         this.props.extra.service.map((service, i) => {
                             if (i == 0 && service) {
                                return this.renderExtra("Полный бак", "Да")
                             } else if (i == 1 && service) {
                                return this.renderExtra("Детское кресло", "Да")
                             } else if (i == 2 && service) {
                                return this.renderExtra("Правый руль", "Да")
                             }
                         })
                     }
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     {this.renderButton(this.props.extra.date, tab, "Итого")}
                </div>
            );
        } else if (this.props.location.city !== "" && this.props.location.point !== "" && this.props.activeTab.total) {
            tab = "total"
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     {this.renderExtra("Пункт выдачи", this.props.location.city, this.props.location.point)}
                     {this.renderExtra("Модель", this.props.car.mark, this.props.car.model)}
                     {this.renderExtra("Цвет", this.props.extra.color)}
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     {this.renderButton(tab, "Заказать")}
                </div>
            );
        } else {
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     <div class="info__point"> 
                         <span>Пункт выдачи</span>
                         <span></span>
                        <span> - </span>
                     </div>
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     <button disabled>Выбрать модель</button>
                </div>
            );
        }

    }
}

export default Info;
