import React from 'react';
import Location from './location';
import Model from './model';
import Extra from './extra';
import Total from './total';
import Info  from './info';

class Order extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            options: {
                city: "",
                point: "",
                model: "3",
                color: "",
                date: "",
                tariff: "",
                service: {
                    tank: "",
                    chair: "",
                    rudder: ""
                },
            }
        }
    }

    changeCity(value) {
        this.setState({
            options: {city: value, point: this.state.options.point}
        })
    }

    changePoint(value) {
        this.setState({
            options: {point: value, city: this.state.options.city}
        })
    }

    changeModel(value) {
        this.setState({
            model: value
        })
    } 
    changeExtra(value) {
        this.setState({
            point: value
        })
    }

    render() {
        console.log(this.state.options.point)
        console.log(this.state.options.city)
        let tab
        if (this.props.activeTab == 'location' || this.state.options.point == '') {
            tab = <Location 
                onChangePoint={(value) => this.changePoint(value)}
                onChangeCity={(value) => this.changeCity(value)}
                options={this.state.options}
            />
        } else if (this.props.activeTab == 'model') {
            tab = <Model onChange={() => this.changeModel()}/>
        } else if (this.props.activeTab == 'extra') {
            tab = <Extra onChange={() => this.changeExtra()}/>
        } else {
            tab = <Total />
        }  
        return (
            <div class="order">
               <div class="board-order">
                    {tab}
                </div>
                <Info options={this.state.options}/>
            </div>
        );
    }
}

export default Order;
