import React from 'react';
import Location from './location';
import Model from './model';
import Extra from './extra';
import Total from './total';
import Info  from './info';

class Order extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        let tab
        if (this.props.activeTab.location || this.state.point == '') {
            tab = <Location 
                onChangePoint={(value) => this.props.onChangePoint(value)}
                onChangeCity={(value) => this.props.onChangeCity(value)}
                location={this.props.location}
                activeTab={this.props.activeTab}
            />
        } else if (this.props.activeTab == 'model') {
            tab = <Model onChange={() => this.changeModel()}/>
        } else if (this.props.activeTab == 'extra') {
            tab = <Extra onChange={() => this.changeExtra()}/>
        } else {
            tab = <Total />
        }
        return (
            <div class="order">
               <div class="board-order">
                    {tab}
                </div>
                <Info 
                    activeTab={this.props.activeTab}
                    location={this.props.location}
                    model={this.props.model}
                    extra={this.props.extra}
                />
            </div>
        );
    }
}

export default Order;
