import React from 'react';
import Location from './location';
import Model from './model';
import Extra from './extra';
import Total from './total';
import Info  from './info';

class Order extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            options: {
                point: "",
                model: "",
                color: "",
                date: "",
                tariff: "",
                service: {
                    tank: "",
                    chair: "",
                    rudder: ""
                },
            }
        }
        this.changePoint = this.changePoint.bind(this)
    }

    changePoint(value) {
        const s = value
        this.setState({
            point: s
        })
    }

    changeModel(value) {
        this.setState({
            model: value
        })
    } 
    changeExtra(value) {
        this.setState({
            point: value
        })
    }

    render() {
        let tab
        if (this.props.activeTab == 'location' || this.state.options.point == '') {
            tab = <Location onChange={() => this.changePoint()}/>
        } else if (this.props.activeTab == 'model') {
            tab = <Model onChange={() => this.changeModel()}/>
        } else if (this.props.activeTab == 'extra') {
            tab = <Extra onChange={() => this.changeExtra()}/>
        } else {
            tab = <Total />
        }  
        return (
            <div class="order">
               <div class="board-order">
                    {tab}
                </div>
                <Info options={this.state.options}/>
            </div>
        );
    }
}

export default Order;
