import React from 'react';
import popupShow from './popupShow';

class Info extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        if (this.props.location.city !== "" && this.props.location.point !== "") {
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     <div class="info__point"> 
                         <span>Пункт выдачи</span>
                         <span></span>
                        <span>{this.props.location.city}, {this.props.location.point}</span>
                     </div>
                     <div class="info__price">
                         <p><b>Цена:</b> от 8 000 до 12 000 ₽</p>
                     </div>
                     <button onClick ={() =>  popupShow()}>Выбрать модель</button>
                </div>
            );
        } else {
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     <div class="info__price">
                         <p><b>Цена:</b></p>
                     </div>
                     <button onClick ={() =>  popupShow()}>Выбрать модель</button>
                </div>
            );
        }

    }
}

export default Info;
