import React from 'react';
import popupShow from './popupShow';

class Info extends React.Component {
    constructor(props) {
        super(props);
    }

    renderButton(tab, text) {
        if (tab) {
            return (
            <button onClick={() => this.props.onClick(tab)}>{text}</button>
            )
        } else {
            return (
                <button disabled>{text}</button>
            )
        }
    }

    render() {
        let tab
        if (this.props.location.city !== "" && this.props.location.point !== "" && this.props.activeTab.location) {
            tab = "model"
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     <div class="info__point"> 
                         <span>Пункт выдачи</span>
                         <span></span>
                        <span>{this.props.location.city}, {this.props.location.point}</span>
                     </div>
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     <button onClick={() => this.props.onClick(tab)}>Выбрать модель</button>
                </div>
            );
        } else if (this.props.location.city !== "" && this.props.location.point !== "" && this.props.activeTab.location) {
            tab = "extra"
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     <div class="info__point"> 
                         <span>Пункт выдачи</span>
                         <span></span>
                        <span>{this.props.location.city}, {this.props.location.point}</span>
                     </div>
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     {renderButton(tab, "Дополнительно")}
                </div>
            );
        } else {
            return (
                <div class="info">  
                     <h2>Ваш заказ:</h2>
                     <div class="info__point"> 
                         <span>Пункт выдачи</span>
                         <span></span>
                        <span> - </span>
                     </div>
                     <div class="info__price">
                         <p><b>Цена:</b> - </p>
                     </div>
                     <button disabled>Выбрать модель</button>
                </div>
            );
        }

    }
}

export default Info;
