import React from 'react';

class Model extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            all:  true,
            ec: false,
            prem: false,
            models: [
                {
                    model: "ELANTRA",
                    price: "12 000 - 25 000 ₽",
                    img: "src/img/image 1.png"
                },
                {
                    model: "i30 N",
                    price: "10 000 - 32 000 ₽",
                    img: "src/img/image 2.png"
                },
                {
                    model: "CRETA",
                    price: "12 000 - 25 000 ₽",
                    img: "src/img/image 3.png"
                },
                {
                    model: "SONATA",
                    price: "10 000 - 32 000 ₽",
                    img: "src/img/image 4.png"
                }
            ]
        };

        this.handleChange = this.handleChange.bind(this);
    }

    // sectionActive() {
    //     console.log(this)
    //     a = this;
    // }

    section(i) {
        return (
            <section class="cars__block" onClick={() => this.sectionActive()}>
                <div class="cars__name">
                    <h2>{this.state.models[i].model}</h2>
                    <p>{this.state.models[i].price}</p>
                </div>
                <img src={this.state.models[i].img} alt=""/>
            </section>
        );
    }

    handleChange(event) {
        const name = event.target.name;

        if (name == "all") {
            this.setState({all: true, ec: false, prem: false,});
        } else if (name == "ec") {
            this.setState({all: false, ec: true, prem: false,});
        } else {
            this.setState({all: false, ec: false, prem: true,});
        }
    }

    render() {
        return (
            <div class="model tab_item">
                <div class="sorting-cars">
                    <input type="radio" checked={this.state.all} onChange={this.handleChange} name="all" id="r1" /><label for="r1"> Все модели </label>
                    <input type="radio" checked={this.state.ec} onChange={this.handleChange} name="ec" id="r2"/><label for="r2"> Эконом </label>
                    <input type="radio" checked={this.state.prem} onChange={this.handleChange} name="prem" id="r3"/><label for="r3"> Премиум </label>
                </div>
                <table class="cars">

                </table>
            </div>
        );
    }
}

export default Model;
